package proselede;

public class LargeNumberFromArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a[]= {2,4,5,6,9,100};
    
    int max=a[0];
    for(int i=1;i<a.length;i++)
    {
    	
		if(a[i]>max);
    	{
    		max=a[i];
    	}
    }
		System.out.println(max);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
